import React, {useEffect} from "react";
import {Pagination, PaginationItem} from '@material-ui/lab';

import useStyles from './styles'
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

//useSelector gets the number of pages

import { getPosts } from "../actions/posts";

const Paginate = ({ page }) => {
    const { numberOfPages } = useSelector((sat))
    const classes = useStyles();
    const dispatch = useDispatch();

    //if there is a page, dispatch the getPost function from the actions
     
    useEffect(() => {
        if(page) dispatch(getPosts());

    }, [page])

    //run the useEffect everytime the page changes

    return (
        <Pagination 
        classes={{ul: classes.ul}}
        count={5}
        page={1}
        variant="outlined"
        color="primary"
        renderItem={(item) => (
            <PaginationItem {...item} component={Link} to={`/posts?page=${1}`} />
        )}
        />
    );
};

export default Paginate;
